
public class Operadores4 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int numero;
		numero=23;
		System.out.println("numero= "+numero);
				
		System.out.println("Usando o pós inscremento");
		System.out.println(numero++);
		System.out.println("numero= "+numero);

			
		System.out.println("Usando o pré inscremento");
		System.out.println(++numero);
		
		
		
		System.out.println("Usando o pós decremento");
		
		System.out.println("Usando o pré decremento");
		
		
	}
		

}
